document.addEventListener("DOMContentLoaded",()=>{const MOBILE_BREAKPOINT=768;const listItems=document.querySelectorAll(".text-links li");const hoverImage=document.getElementById("hoverImage");const defaultImageSrc=hoverImage?hoverImage.src:null;const isMobile=()=>window.innerWidth<=MOBILE_BREAKPOINT;const checkAndToggleFeatures=()=>{if(isMobile()){if(typeof cbpBS!=="undefined"&&typeof cbpBS.init==="function"){}
listItems.forEach((item)=>{const linkElement=item.querySelector("a");if(!linkElement)return;item.style.cursor="default";linkElement.textContent=linkElement.getAttribute("data-original-text")||linkElement.textContent})}else{setupDesktopHover()}};const setupDesktopHover=()=>{listItems.forEach((item)=>{const linkElement=item.querySelector("a");if(!linkElement)return;if(!linkElement.getAttribute("data-original-text")){linkElement.setAttribute("data-original-text",linkElement.textContent)}
const originalText=linkElement.getAttribute("data-original-text");const imageFileName=item.dataset.image;item.style.cursor="pointer";item.onmouseenter=null;item.onmouseleave=null;item.onmouseenter=()=>{if(!isMobile()&&hoverImage){hoverImage.src=imageFileName;linkElement.textContent=imageFileName}};item.onmouseleave=()=>{if(!isMobile()&&hoverImage){hoverImage.src=defaultImageSrc;linkElement.textContent=originalText}}})};checkAndToggleFeatures();let resizeTimeout;window.addEventListener("resize",()=>{clearTimeout(resizeTimeout);resizeTimeout=setTimeout(checkAndToggleFeatures,200)})})